//CRUD METHODS


/**
 * contattiService.create
 *   @description CRUD ACTION create
 *
 */
app.post(properties.api + '/contattis', function (req, res) {
	obj = new db_Contatti_db.contatti(req.body);
	obj.save(function (err) {
		if (err) return handleError(err, res);
		res.send(obj);
	});
});

/**
 * contattiService.delete
 *   @description CRUD ACTION delete
 *   @param ObjectId id Id
 *
 */
app['delete'](properties.api + '/contattis/:id', function (req, res) {
	db_Contatti_db.contatti.findByIdAndRemove(req.params.id, function (err) {
		  if (err) return handleError(err, res);
		  res.send(err);
	});
});

/**
 * contattiService.get
 *   @description CRUD ACTION get
 *   @param ObjectId id Id 
 *
 */
app.get(properties.api + '/contattis/:id', function (req, res) {
	db_Contatti_db.contatti.findOne({_id:req.params.id}).exec(function (err, obj) {
		if (err) return handleError(err, res);
		res.send(obj);
	});
});

/**
 * contattiService.list
 *   @description CRUD ACTION list
 *
 */
app.get(properties.api + '/contattis', function (req, res) {
	db_Contatti_db.contatti.find().exec(function (err, list) {
		if (err) return handleError(err, res);
		res.send(list);
	});
});

/**
 * contattiService.update
 *   @description CRUD ACTION update
 *   @param ObjectId id Id
 *
 */
app.post(properties.api + '/contattis/:id', function (req, res) {
	db_Contatti_db.contatti.findByIdAndUpdate(req.params.id, req.body, {'new': true}, function (err, obj) {
		if (err) return handleError(err, res);
		res.send(obj);
	});
});


